import React from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  SafeAreaView,
  Share,
} from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import { Ionicons } from '@expo/vector-icons';

/**
 * ResultScreen
 * Displays quiz results with score and explanations
 */
const ResultScreen = ({ route, navigation }) => {
  const { score, total, answers, topic } = route.params;
  const percentage = Math.round((score / total) * 100);

  const getScoreMessage = () => {
    if (percentage >= 80) return { emoji: 'ðŸŽ‰', message: 'Excellent!', color: '#4CAF50' };
    if (percentage >= 60) return { emoji: 'ðŸ‘', message: 'Good Job!', color: '#66BB6A' };
    if (percentage >= 40) return { emoji: 'ðŸ“š', message: 'Keep Learning!', color: '#FFA726' };
    return { emoji: 'ðŸ’ª', message: 'Keep Trying!', color: '#EF5350' };
  };

  const scoreInfo = getScoreMessage();

  const handleShare = async () => {
    try {
      await Share.share({
        message: `I scored ${score}/${total} (${percentage}%) on the ${topic} quiz in Ä€ithreya! ðŸŽ“âš–ï¸`,
      });
    } catch (error) {
      console.error('Error sharing:', error);
    }
  };

  const handleRetry = () => {
    navigation.replace('Quiz', { topic, topicId: topic.toLowerCase().replace(/\s+/g, '-') });
  };

  const handleHome = () => {
    navigation.navigate('TopicSelection');
  };

  return (
    <LinearGradient
      colors={['#E8EAF6', '#F3E5F5', '#FCE4EC']}
      style={styles.gradient}
    >
      <SafeAreaView style={styles.safeArea}>
        <ScrollView 
          style={styles.scrollView}
          contentContainerStyle={styles.scrollContent}
          showsVerticalScrollIndicator={false}
        >
          <View style={styles.scoreCard}>
            <Text style={styles.scoreEmoji}>{scoreInfo.emoji}</Text>
            <Text style={[styles.scoreMessage, { color: scoreInfo.color }]}>
              {scoreInfo.message}
            </Text>
            <Text style={styles.scoreText}>
              {score}/{total}
            </Text>
            <Text style={styles.percentageText}>{percentage}%</Text>
            <View style={styles.topicBadge}>
              <Text style={styles.topicBadgeText}>{topic}</Text>
            </View>
          </View>

          <View style={styles.statsContainer}>
            <View style={styles.statBox}>
              <Ionicons name="checkmark-circle" size={32} color="#4CAF50" />
              <Text style={styles.statNumber}>{score}</Text>
              <Text style={styles.statLabel}>Correct</Text>
            </View>
            <View style={styles.statBox}>
              <Ionicons name="close-circle" size={32} color="#EF5350" />
              <Text style={styles.statNumber}>{total - score}</Text>
              <Text style={styles.statLabel}>Incorrect</Text>
            </View>
            <View style={styles.statBox}>
              <Ionicons name="trophy" size={32} color="#FFA726" />
              <Text style={styles.statNumber}>{percentage}%</Text>
              <Text style={styles.statLabel}>Score</Text>
            </View>
          </View>

          <Text style={styles.sectionTitle}>Review Answers</Text>

          {answers.map((answer, index) => (
            <View key={index} style={styles.answerCard}>
              <View style={styles.answerHeader}>
                <View style={styles.questionNumberBadge}>
                  <Text style={styles.questionNumberText}>Q{index + 1}</Text>
                </View>
                {answer.isCorrect ? (
                  <View style={styles.correctBadge}>
                    <Ionicons name="checkmark" size={16} color="#FFFFFF" />
                    <Text style={styles.badgeText}>Correct</Text>
                  </View>
                ) : (
                  <View style={styles.incorrectBadge}>
                    <Ionicons name="close" size={16} color="#FFFFFF" />
                    <Text style={styles.badgeText}>Incorrect</Text>
                  </View>
                )}
              </View>

              <Text style={styles.questionText}>{answer.question}</Text>

              {!answer.isCorrect && (
                <View style={styles.answerInfo}>
                  <Text style={styles.answerInfoLabel}>Your answer was incorrect</Text>
                  <Text style={styles.correctAnswerLabel}>
                    Correct answer: {['A', 'B', 'C', 'D'][answer.correctAnswer]}
                  </Text>
                </View>
              )}

              <View style={styles.explanationContainer}>
                <Text style={styles.explanationLabel}>
                  <Ionicons name="bulb-outline" size={16} color="#7E57C2" /> Explanation
                </Text>
                <Text style={styles.explanationText}>{answer.explanation}</Text>
                {answer.legalReference && (
                  <View style={styles.referenceTag}>
                    <Ionicons name="document-text" size={14} color="#7E57C2" />
                    <Text style={styles.referenceText}>{answer.legalReference}</Text>
                  </View>
                )}
              </View>
            </View>
          ))}

          <View style={styles.actionButtons}>
            <TouchableOpacity
              style={[styles.button, styles.shareButton]}
              onPress={handleShare}
            >
              <Ionicons name="share-social" size={20} color="#5C6BC0" />
              <Text style={styles.shareButtonText}>Share Results</Text>
            </TouchableOpacity>

            <TouchableOpacity
              style={[styles.button, styles.retryButton]}
              onPress={handleRetry}
            >
              <Ionicons name="refresh" size={20} color="#FFFFFF" />
              <Text style={styles.retryButtonText}>Retry Quiz</Text>
            </TouchableOpacity>

            <TouchableOpacity
              style={[styles.button, styles.homeButton]}
              onPress={handleHome}
            >
              <Ionicons name="home" size={20} color="#FFFFFF" />
              <Text style={styles.homeButtonText}>New Topic</Text>
            </TouchableOpacity>
          </View>
        </ScrollView>
      </SafeAreaView>
    </LinearGradient>
  );
};

const styles = StyleSheet.create({
  gradient: {
    flex: 1,
  },
  safeArea: {
