import React from 'react';
import { TouchableOpacity, Text, StyleSheet, View } from 'react-native';

/**
 * OptionButton Component
 * MCQ option button with selection states
 */
const OptionButton = ({ 
  option, 
  label, 
  isSelected, 
  isCorrect, 
  isIncorrect, 
  showAnswer, 
  onPress, 
  disabled 
}) => {
  const getButtonStyle = () => {
    if (showAnswer) {
      if (isCorrect) return styles.correctButton;
      if (isIncorrect) return styles.incorrectButton;
    }
    if (isSelected && !showAnswer) return styles.selectedButton;
    return styles.defaultButton;
  };

  const getTextStyle = () => {
    if (showAnswer && (isCorrect || isIncorrect)) return styles.answerText;
    if (isSelected) return styles.selectedText;
    return styles.defaultText;
  };

  return (
    <TouchableOpacity
      style={[styles.button, getButtonStyle()]}
      onPress={onPress}
      disabled={disabled}
      activeOpacity={0.7}
    >
      <View style={styles.labelContainer}>
        <Text style={[styles.label, getTextStyle()]}>{label}</Text>
      </View>
      <Text style={[styles.optionText, getTextStyle()]}>{option}</Text>
      {showAnswer && isCorrect && <Text style={styles.emoji}>✓</Text>}
      {showAnswer && isIncorrect && <Text style={styles.emoji}>✗</Text>}
    </TouchableOpacity>
  );
};

const styles = StyleSheet.create({
  button: {
    flexDirection: 'row',
    alignItems: 'center',
    padding: 16,
    borderRadius: 12,
    marginBottom: 12,
    borderWidth: 2,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 1 },
    shadowOpacity: 0.05,
    shadowRadius: 4,
    elevation: 2,
  },
  defaultButton: {
    backgroundColor: '#FFFFFF',
    borderColor: '#E0E0E0',
  },
  selectedButton: {
    backgroundColor: '#E3F2FD',
    borderColor: '#90CAF9',
  },
  correctButton: {
    backgroundColor: '#E8F5E9',
    borderColor: '#66BB6A',
  },
  incorrectButton: {
    backgroundColor: '#FFEBEE',
    borderColor: '#EF5350',
  },
  labelContainer: {
    width: 32,
    height: 32,
    borderRadius: 16,
    backgroundColor: '#F5F5F5',
    justifyContent: 'center',
    alignItems: 'center',
    marginRight: 12,
  },
  label: {
    fontSize: 16,
    fontWeight: '700',
  },
  defaultText: {
    color: '#333',
  },
  selectedText: {
    color: '#1976D2',
  },
  answerText: {
    color: '#333',
  },
  optionText: {
    flex: 1,
    fontSize: 15,
    lineHeight: 22,
  },
  emoji: {
    fontSize: 20,
    marginLeft: 8,
  },
});

export default OptionButton;
