import React from 'react';
import { View, Image, StyleSheet, ActivityIndicator, Text } from 'react-native';

/**
 * AIImageDisplay Component
 * Displays AI-generated image with loading state
 */
const AIImageDisplay = ({ imageUrl, imagePrompt, isLoading }) => {
  const [imageLoaded, setImageLoaded] = React.useState(false);
  const [imageError, setImageError] = React.useState(false);

  if (isLoading) {
    return (
      <View style={styles.loadingContainer}>
        <ActivityIndicator size="large" color="#A7C7E7" />
        <Text style={styles.loadingText}>Generating illustration...</Text>
      </View>
    );
  }

  if (imageError || !imageUrl) {
    return (
      <View style={styles.placeholderContainer}>
        <Text style={styles.placeholderIcon}>⚖️</Text>
        <Text style={styles.placeholderText}>Legal Quiz</Text>
      </View>
    );
  }

  return (
    <View style={styles.container}>
      <Image
        source={{ uri: imageUrl }}
        style={styles.image}
        onLoad={() => setImageLoaded(true)}
        onError={() => setImageError(true)}
        resizeMode="cover"
      />
      {!imageLoaded && (
        <View style={styles.imageLoadingOverlay}>
          <ActivityIndicator size="small" color="#A7C7E7" />
        </View>
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    width: '100%',
    height: 240,
    borderRadius: 16,
    overflow: 'hidden',
    backgroundColor: '#F5F5F5',
    marginBottom: 20,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.1,
    shadowRadius: 8,
    elevation: 3,
  },
  image: {
    width: '100%',
    height: '100%',
  },
  loadingContainer: {
    width: '100%',
    height: 240,
    borderRadius: 16,
    backgroundColor: '#F5F5F5',
    justifyContent: 'center',
    alignItems: 'center',
    marginBottom: 20,
  },
  loadingText: {
    marginTop: 12,
    fontSize: 14,
    color: '#888',
    fontWeight: '500',
  },
  placeholderContainer: {
    width: '100%',
    height: 240,
    borderRadius: 16,
    backgroundColor: '#E8F5E9',
    justifyContent: 'center',
    alignItems: 'center',
    marginBottom: 20,
  },
  placeholderIcon: {
    fontSize: 60,
    marginBottom: 10,
  },
  placeholderText: {
    fontSize: 16,
    color: '#4CAF50',
    fontWeight: '600',
  },
  imageLoadingOverlay: {
    ...StyleSheet.absoluteFillObject,
    backgroundColor: 'rgba(255, 255, 255, 0.8)',
    justifyContent: 'center',
    alignItems: 'center',
  },
});

export default AIImageDisplay;
