import axios from 'axios';

// Configure your backend URL
const API_BASE_URL = __DEV__ 
  ? 'http://localhost:3000/api' 
  : 'https://your-production-api.com/api';

const api = axios.create({
  baseURL: API_BASE_URL,
  timeout: 30000, // 30 seconds for AI generation
  headers: {
    'Content-Type': 'application/json',
  },
});

/**
 * Fetch available quiz topics
 */
export const fetchTopics = async () => {
  try {
    const response = await api.get('/quiz/topics');
    return response.data.data.topics;
  } catch (error) {
    console.error('Error fetching topics:', error);
    throw new Error(
      error.response?.data?.error?.message || 
      'Failed to load topics. Please check your connection.'
    );
  }
};

/**
 * Generate quiz for a specific topic
 * @param {string} topic - The legal topic
 * @param {number} count - Number of questions (default: 5)
 */
export const generateQuiz = async (topic, count = 5) => {
  try {
    console.log(`Requesting quiz for: ${topic}`);
    const response = await api.get('/quiz/generate', {
      params: { topic, count }
    });
    return response.data.data;
  } catch (error) {
    console.error('Error generating quiz:', error);
    throw new Error(
      error.response?.data?.error?.message || 
      'Failed to generate quiz. Please try again.'
    );
  }
};

/**
 * Health check
 */
export const checkApiHealth = async () => {
  try {
    const response = await axios.get(`${API_BASE_URL.replace('/api', '')}/health`);
    return response.data;
  } catch (error) {
    throw new Error('API is unreachable');
  }
};

export default api;
