import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  ActivityIndicator,
  SafeAreaView,
  Alert,
} from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import ProgressBar from '../components/ProgressBar';
import AIImageDisplay from '../components/AIImageDisplay';
import OptionButton from '../components/OptionButton';
import ExplanationBox from '../components/ExplanationBox';
import { generateQuiz } from '../services/apiService';

/**
 * QuizScreen
 * Main quiz interface with AI images and MCQs
 */
const QuizScreen = ({ route, navigation }) => {
  const { topic, topicId } = route.params;
  
  const [loading, setLoading] = useState(true);
  const [questions, setQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [showExplanation, setShowExplanation] = useState(false);
  const [score, setScore] = useState(0);
  const [answers, setAnswers] = useState([]);

  useEffect(() => {
    loadQuiz();
  }, []);

  const loadQuiz = async () => {
    try {
      setLoading(true);
      const quizData = await generateQuiz(topic, 5);
      setQuestions(quizData.questions);
    } catch (error) {
      Alert.alert('Error', error.message, [
        { text: 'Go Back', onPress: () => navigation.goBack() }
      ]);
    } finally {
      setLoading(false);
    }
  };

  const currentQuestion = questions[currentQuestionIndex];

  const handleAnswerSelect = (answerIndex) => {
    if (showExplanation) return; // Already answered
    
    setSelectedAnswer(answerIndex);
    setShowExplanation(true);

    const isCorrect = answerIndex === currentQuestion.correctAnswer;
    if (isCorrect) {
      setScore(score + 1);
    }

    setAnswers([...answers, {
      questionId: currentQuestion.id,
      question: currentQuestion.question,
      selectedAnswer: answerIndex,
      correctAnswer: currentQuestion.correctAnswer,
      isCorrect,
      explanation: currentQuestion.explanation,
      legalReference: currentQuestion.legalReference,
    }]);
  };

  const handleNextQuestion = () => {
    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
      setSelectedAnswer(null);
      setShowExplanation(false);
    } else {
      // Quiz complete
      navigation.replace('Result', {
        score,
        total: questions.length,
        answers,
        topic,
      });
    }
  };

  if (loading) {
    return (
      <LinearGradient
        colors={['#E8EAF6', '#F3E5F5']}
        style={styles.gradient}
      >
        <View style={styles.loadingContainer}>
          <ActivityIndicator size="large" color="#5C6BC0" />
          <Text style={styles.loadingText}>Generating your quiz...</Text>
          <Text style={styles.loadingSubtext}>Creating AI illustrations</Text>
        </View>
      </LinearGradient>
    );
  }

  if (!currentQuestion) {
    return null;
  }

  return (
    <LinearGradient
      colors={['#E8EAF6', '#F3E5F5']}
      style={styles.gradient}
    >
      <SafeAreaView style={styles.safeArea}>
        <ProgressBar 
          current={currentQuestionIndex + 1} 
          total={questions.length} 
        />

        <ScrollView 
          style={styles.scrollView}
          contentContainerStyle={styles.scrollContent}
          showsVerticalScrollIndicator={false}
        >
          <View style={styles.topicBadge}>
            <Text style={styles.topicBadgeText}>{topic}</Text>
          </View>

          <AIImageDisplay
            imageUrl={currentQuestion.image?.url}
            imagePrompt={currentQuestion.imagePrompt}
            isLoading={false}
          />

          <View style={styles.questionCard}>
            <Text style={styles.questionText}>{currentQuestion.question}</Text>
          </View>

          <View style={styles.optionsContainer}>
            {currentQuestion.options.map((option, index) => (
              <OptionButton
                key={index}
                option={option}
                label={['A', 'B', 'C', 'D'][index]}
                isSelected={selectedAnswer === index}
                isCorrect={showExplanation && index === currentQuestion.correctAnswer}
                isIncorrect={showExplanation && selectedAnswer === index && index !== currentQuestion.correctAnswer}
                showAnswer={showExplanation}
                onPress={() => handleAnswerSelect(index)}
                disabled={showExplanation}
              />
            ))}
          </View>

          <ExplanationBox
            explanation={currentQuestion.explanation}
            legalReference={currentQuestion.legalReference}
            isVisible={showExplanation}
          />

          {showExplanation && (
            <TouchableOpacity
              style={styles.nextButton}
              onPress={handleNextQuestion}
            >
              <Text style={styles.nextButtonText}>
                {currentQuestionIndex < questions.length - 1 ? 'Next Question' : 'View Results'}
              </Text>
            </TouchableOpacity>
          )}
        </ScrollView>
      </SafeAreaView>
    </LinearGradient>
  );
};

const styles = StyleSheet.create({
  gradient: {
    flex: 1,
  },
  safeArea: {
    flex: 1,
  },
  loadingContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 20,
  },
  loadingText: {
    marginTop: 16,
    fontSize: 18,
    fontWeight: '600',
    color: '#5C6BC0',
  },
  loadingSubtext: {
    marginTop: 8,
    fontSize: 14,
    color: '#888',
  },
  scrollView: {
    flex: 1,
  },
  scrollContent: {
    padding: 20,
    paddingBottom: 40,
  },
  topicBadge: {
    alignSelf: 'flex-start',
    backgroundColor: 'rgba(92, 107, 192, 0.15)',
    paddingHorizontal: 16,
    paddingVertical: 8,
    borderRadius: 20,
    marginBottom: 16,
  },
  topicBadgeText: {
    fontSize: 13,
    fontWeight: '700',
    color: '#5C6BC0',
  },
  questionCard: {
    backgroundColor: '#FFFFFF',
    borderRadius: 16,
    padding: 20,
    marginBottom: 20,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.08,
    shadowRadius: 8,
    elevation: 3,
  },
  questionText: {
    fontSize: 18,
    fontWeight: '600',
    color: '#333',
    lineHeight: 26,
  },
  optionsContainer: {
    marginBottom: 8,
  },
  nextButton: {
    backgroundColor: '#5C6BC0',
    borderRadius: 12,
    padding: 18,
    alignItems: 'center',
    marginTop: 20,
    shadowColor: '#5C6BC0',
    shadowOffset: { width: 0, height: 4 },
    shadowOpacity: 0.3,
    shadowRadius: 8,
    elevation: 5,
  },
  nextButtonText: {
    fontSize: 16,
    fontWeight: '700',
    color: '#FFFFFF',
  },
});

export default QuizScreen;
