import React from 'react';
import { View, Text, StyleSheet, ScrollView } from 'react-native';
import { Ionicons } from '@expo/vector-icons';

/**
 * ExplanationBox Component
 * Displays legal explanation with reference
 */
const ExplanationBox = ({ explanation, legalReference, isVisible }) => {
  if (!isVisible) return null;

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Ionicons name="information-circle" size={24} color="#5C6BC0" />
        <Text style={styles.headerText}>Explanation</Text>
      </View>
      
      <ScrollView style={styles.contentScroll} nestedScrollEnabled>
        <Text style={styles.explanationText}>{explanation}</Text>
        
        {legalReference && (
          <View style={styles.referenceContainer}>
            <Ionicons name="document-text-outline" size={18} color="#7E57C2" />
            <Text style={styles.referenceText}>
              Legal Reference: {legalReference}
            </Text>
          </View>
        )}
      </ScrollView>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    backgroundColor: '#F3F4FB',
    borderRadius: 12,
    padding: 16,
    marginTop: 16,
    borderLeftWidth: 4,
    borderLeftColor: '#5C6BC0',
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.08,
    shadowRadius: 6,
    elevation: 3,
    maxHeight: 200,
  },
  header: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 12,
  },
  headerText: {
    fontSize: 16,
    fontWeight: '700',
    color: '#5C6BC0',
    marginLeft: 8,
  },
  contentScroll: {
    maxHeight: 140,
  },
  explanationText: {
    fontSize: 14,
    lineHeight: 22,
    color: '#444',
    marginBottom: 12,
  },
  referenceContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: '#E8EAF6',
    padding: 10,
    borderRadius: 8,
    marginTop: 4,
  },
  referenceText: {
    fontSize: 13,
    color: '#7E57C2',
    fontWeight: '600',
    marginLeft: 6,
    flex: 1,
  },
});

export default ExplanationBox;
